var fs=require('fs');
var readStream=fs.createReadStream('insurance.csv');
var data="";
readStream.on('data',function(chunk){
	console.log('Received %d bytes of data',chunk.length);
	readStream.pause();
	console.log('There will no data flow for 2 seconds');
	setTimeout(function(){
		console.log('Now data will start flowing again');
		readStream.resume();
	},2000);
	
	
});


